import React from 'react'
import memesData from '../memesData'
import {useState} from 'react'

export default function Meme() {

    const[meme, setMeme] = useState({
        topText: "",
        bottomText: "",
        randomImg:""
    })

    const [allMemeImages, setAllMemeImages] = useState(memesData)

    function getImage(){
        const memeArray = allMemeImages.data.memes
        const randomNo = Math.floor(Math.random() * memeArray.length)
        const memeUrl = memeArray[randomNo].url

        setMeme(prevMeme=>({
            ...prevMeme,
            randomImg: memeUrl
        })
        )
    }

    function handleChange(event){
        const {name, value } = event.target
        setMeme(prevMeme => ({
            ...prevMeme,
            [name]: value
        }))

    }
    

  return (
    <main>
        <div className='form'>
            <input className='form-input' type="text" placeholder='Top-text' onClick={handleChange} name= "topText" value={meme.topText}/>
            <input className='form-input' type="text" placeholder='Bottom-text' onClick={handleChange} name= "bottomText" value={meme.bottomText}/>
            <button onClick={getImage} className='form-btn'>Get a new meme image  ðŸ–¼ </button>
        </div>
        <div className="meme">
                <img src={meme.randomImg} className="meme-image" />
                <h2 className="meme-text top">{meme.topText}</h2>
                <h2 className="meme-text bottom">{meme.bottomText}</h2>
            </div>
    </main>
  )
}
